mapper.insert:
INSERT INTO member
(   member_id
   ,member_pw
   ,member_name
   ,member_email
   ,member_gender
   ,member_addr
   ,member_phone_number
   ,member_birth
   ,member_sign
) VALUES (
    #{memberId}
   ,#{memberPw}
   ,#{memberName}
   ,#{memberEmail}
   ,#{memberGender}
   ,#{memberAddr}
   ,#{memberPhone_number}
   ,#{memberBirth}
   , CURDATE()
);

